import 'dart:convert';
import 'package:http/http.dart' as http;

Future<void> main() async {
  final uri = Uri.parse('https://api.myquran.com/v3/sholat/kabkota/cari');
  final headers = {
    'Accept': 'application/json',
    'Content-Type': 'application/json',
  };
  const payload = '{"keyword":"kediri"}';
  final response = await http.post(uri, headers: headers, body: payload);
  if (response.statusCode == 200) {
    final data = jsonDecode(response.body);
    print(data);
  } else {
    print('Request failed: ${response.statusCode}');
  }
}

package main

import (
	"fmt"
	"io"
	"net/http"
	"strings"
)

func main() {
	client := &http.Client{}
	payload := strings.NewReader(`{"keyword":"kediri"}`)
	req, err := http.NewRequest("POST", "https://api.myquran.com/v3/sholat/kabkota/cari", payload)
	if err != nil {
		panic(err)
	}
	req.Header.Set("Accept", "application/json")
	req.Header.Set("Content-Type", "application/json")

	res, err := client.Do(req)
	if err != nil {
		panic(err)
	}
	defer res.Body.Close()

	bodyBytes, _ := io.ReadAll(res.Body)
	fmt.Println(string(bodyBytes))
}

using System;
using System.Net.Http;
using System.Net.Http.Headers;
using System.Text;
using System.Threading.Tasks;

class Program
{
    static async Task Main()
    {
        using var client = new HttpClient();
        using var request = new HttpRequestMessage(HttpMethod.Post, "https://api.myquran.com/v3/sholat/kabkota/cari");
        request.Headers.Accept.Add(new MediaTypeWithQualityHeaderValue("application/json"));
        request.Content = new StringContent("{\"keyword\":\"kediri\"}", Encoding.UTF8, "application/json");
        using var response = await client.SendAsync(request);
        response.EnsureSuccessStatusCode();
        var responseBody = await response.Content.ReadAsStringAsync();
        Console.WriteLine(responseBody);
    }
}

